def is_truthy(value):
    """
    Helper to interpret common truthy strings from the environment.
    Returns True for: '1', 'true', 'yes', 'on' (case-insensitive).
    """
    if value is None:
        return False
    return str(value).strip().lower() in ("1", "true", "yes", "on")


