def real_client_ip():
    """
    Helper function to get the real client IP address behind proxies.
    Checks Cloudflare CF-Connecting-IP, then X-Forwarded-For, then remote_addr.
    Returns "unknown" if all are missing.
    """
    # Cloudflare provides CF-Connecting-IP header
    cf_ip = request.headers.get("CF-Connecting-IP", "").strip()
    if cf_ip:
        return cf_ip
    
    # X-Forwarded-For can contain multiple IPs (client, proxy1, proxy2, ...)
    # Take the first one (original client)
    xff = request.headers.get("X-Forwarded-For", "").strip()
    if xff:
        # X-Forwarded-For can be comma-separated list
        first_ip = xff.split(",")[0].strip()
        if first_ip:
            return first_ip
    
    # Fallback to remote_addr (works after ProxyFix)
    if request.remote_addr:
        return request.remote_addr
    
    return "unknown"


# --- DB Check Route (protected with token, enabled in all environments) ---
# Usage: curl -H "Authorization: Bearer $DBCHECK_TOKEN" https://your-app.onrender.com/dbcheck
# 
# To set up permanently:
# Set DBCHECK_TOKEN in Render Environment (do not hardcode).
# Redeploy the service after setting the environment variable.
@app.get("/dbcheck")
@limiter.limit("10 per minute", key_func=real_client_ip)
def dbcheck():
    """Database status endpoint - protected by DBCHECK_TOKEN"""
    # Production kill-switch: require ENABLE_DBCHECK=1 in production
    if is_production and not is_truthy(os.getenv("ENABLE_DBCHECK", "0")):
        abort(404)
    
    # Read expected token from environment variable (single source of truth)
    expected_token = os.getenv("DBCHECK_TOKEN")
    
    # If DBCHECK_TOKEN is missing/empty, return 500 with server_misconfigured error
    if not expected_token:
        # In production, return 404 to hide the endpoint, but log the issue
        if is_production:
            logger.warning("DBCHECK_ROUTE_ACCESS=denied reason=DBCHECK_TOKEN_not_set path=%s", request.path)
            abort(404)
        return jsonify({"error": "server_misconfigured", "message": "DBCHECK_TOKEN environment variable is not set"}), 500
    
    # Get provided token from Authorization header
    auth_header = request.headers.get("Authorization", "")
    if not auth_header.startswith("Bearer "):
        # Return 404 in production to reduce endpoint discovery, 403 in non-production
        if is_production:
            logger.warning("DBCHECK_ROUTE_ACCESS=denied reason=missing_authorization_header path=%s", request.path)
            abort(404)
        return jsonify({"error": "forbidden"}), 403
    
    provided_token = auth_header[7:].strip()
    
    # Validate token using constant-time comparison
    if not provided_token or not secrets.compare_digest(provided_token, expected_token):
        # Return 404 in production to reduce endpoint discovery, 403 in non-production
        if is_production:
            logger.warning("DBCHECK_ROUTE_ACCESS=denied reason=token_mismatch path=%s", request.path)
            abort(404)
        return jsonify({"error": "forbidden"}), 403
    
    # Token validated successfully
    logger.info("DBCHECK_ROUTE_ACCESS=allowed")
    
    try:
        conn = db.get_conn()
        cursor = conn.cursor()
        
        # Determine DB type
        db_type = "postgres" if db.USE_POSTGRES else "sqlite"
        
        # Get Python version
        python_version = f"{sys.version_info.major}.{sys.version_info.minor}.{sys.version_info.micro}"
        
        # Get psycopg version if installed
        psycopg_version = None
        try:
            import psycopg  # pyright: ignore[reportMissingImports]
            psycopg_version = getattr(psycopg, "__version__", "unknown")
        except ImportError:
            pass
        
        # Get database URL scheme
        database_url = os.getenv("DATABASE_URL", "")
        database_url_scheme = ""
        if database_url:
            parts = database_url.split(":", 1)
            database_url_scheme = parts[0] if parts else ""
        
        conn.close()
        
        response = jsonify({
            "ok": True,
            "db_type": db_type,
            "database_url_scheme": database_url_scheme,
            "python_version": python_version,
            "psycopg_version": psycopg_version,
            "time_utc": datetime.now(timezone.utc).isoformat().replace("+00:00", "Z"),
            "version": _app_version(),
        })
        # Add Cache-Control: no-store header
        response.headers["Cache-Control"] = "no-store"
        return response
    except Exception as e:
        return jsonify({"error": str(e)}), 500

