# --- Helper: Detect if running on Render production ---
def _is_render_prod():
    """
    Detects if the application is running on Render production environment.
    Returns True if any Render-specific environment variables are set and non-empty.
    """
    render_vars = ["RENDER", "RENDER_SERVICE_ID", "RENDER_EXTERNAL_URL", "RENDER_GIT_COMMIT"]
    return any(os.getenv(var) for var in render_vars)


# --- Helper: Get app version identifier ---
def _app_version():
    """
    Returns a short version identifier (7-12 chars) from environment variables.
    Priority: RENDER_GIT_COMMIT > GIT_SHA > COMMIT_SHA > "unknown"
    Never raises exceptions.
    """
    try:
        version_vars = ["RENDER_GIT_COMMIT", "GIT_SHA", "COMMIT_SHA"]
        for var in version_vars:
            value = os.getenv(var)
            if value:
                # Return first 12 characters (or less if shorter)
                return value[:12] if len(value) > 12 else value
        return "unknown"
    except Exception:
        return "unknown"


# --- Readiness Check Endpoint (DB connectivity + required tables) ---
@app.get("/__ready")
def readiness_check():
    """
    Strict readiness check endpoint for Render/UptimeRobot monitoring.
    FAILS (HTTP 500) if DB is not reachable, not Postgres, or required tables are missing.
    
    Validations:
    1. DATABASE_URL (or fallback DB_URL) must exist
    2. DB scheme must be postgres/postgresql (SQLite rejected)
    3. Must be able to connect to Postgres (connect_timeout=5)
    4. Must be able to run SELECT 1
    5. users table must exist in public schema
    
    Example: curl -i https://<service>/__ready
    """
    import time
    start_time = time.perf_counter()
    conn = None
    cursor = None
    
    try:
        # Validation 1: Check DATABASE_URL/DB_URL exists
        db_url = os.getenv("DATABASE_URL") or os.getenv("DB_URL")
        if not db_url:
            app.logger.error("__ready: DATABASE_URL and DB_URL both missing")
            return jsonify({"ok": False, "error": "DB_URL_MISSING"}), 500
        
        # Validation 2: Check DB scheme is postgres/postgresql (reject SQLite)
        db_url_lower = db_url.lower().strip()
        if not (db_url_lower.startswith("postgres://") or db_url_lower.startswith("postgresql://")):
            scheme = db_url.split("://")[0] if "://" in db_url else "unknown"
            app.logger.error("__ready: Invalid DB scheme '%s', expected postgres/postgresql", scheme)
            return jsonify({
                "ok": False,
                "error": "DB_TYPE_MISMATCH",
                "expected": "postgres",
                "got": scheme
            }), 500
        
        # Validation 3 & 4: Connect to Postgres and run SELECT 1
        try:
            import psycopg
            from psycopg.rows import dict_row
            
            # Normalize URL using proper parsing
            source_url = db_url
            if source_url.startswith("postgres://"):
                source_url = "postgresql://" + source_url[len("postgres://"):]
            
            parsed = urlparse(source_url)
            qs = dict(parse_qsl(parsed.query, keep_blank_values=True))
            
            render_prod = _is_render_prod()
            if render_prod and "sslmode" not in qs:
                qs["sslmode"] = "require"
            
            normalized_db_url = urlunparse(parsed._replace(query=urlencode(qs)))
            
            conn = psycopg.connect(normalized_db_url, row_factory=dict_row, connect_timeout=5)
            cursor = conn.cursor()
            cursor.execute("SELECT 1")
            cursor.fetchone()
        except Exception as connect_error:
            app.logger.exception("__ready: Failed to connect to Postgres or execute SELECT 1")
            if _is_render_prod():
                # Production: don't leak error details
                return jsonify({"ok": False, "error": "DB_CONNECT_FAILED"}), 500
            else:
                # Local/dev: include detail for debugging
                return jsonify({
                    "ok": False,
                    "error": "DB_CONNECT_FAILED",
                    "detail": type(connect_error).__name__
                }), 500
        
        # Validation 5: Check users table exists
        try:
            cursor.execute(
                "SELECT to_regclass(%s) IS NOT NULL as exists",
                ("public.users",)
            )
            result = cursor.fetchone()
            exists = result['exists'] if isinstance(result, dict) else result[0]
            
            if not exists:
                app.logger.error("__ready: users table does not exist in public schema")
                return jsonify({
                    "ok": False,
                    "error": "MISSING_TABLE",
                    "table": "users"
                }), 500
        except Exception as table_check_error:
            app.logger.exception("__ready: Failed to check if users table exists")
            return jsonify({
                "ok": False,
                "error": "TABLE_CHECK_FAILED",
                "detail": type(table_check_error).__name__
            }), 500
        
        # All checks passed
        latency_ms = int((time.perf_counter() - start_time) * 1000)
        return jsonify({
            "ok": True,
            "db": "postgres",
            "latency_ms": latency_ms,
            "version": _app_version()
        }), 200
        
    except Exception as e:
        app.logger.exception("__ready: Unexpected error during readiness check")
        return jsonify({
            "ok": False,
            "error": "UNEXPECTED_ERROR",
            "detail": type(e).__name__
        }), 500
        
    finally:
        # Clean up connections
        try:
            if cursor is not None:
                cursor.close()
        except Exception:
            pass
        try:
            if conn is not None:
                conn.close()
        except Exception:
            pass


# --- Diagnostics Endpoint (token-protected) ---
@app.get("/__diag")
@limiter.limit("5 per minute", key_func=real_client_ip)
def __diag():
    """
    Protected diagnostics endpoint for troubleshooting.
    Requires X-DIAG-TOKEN header matching DIAG_TOKEN environment variable.
    Optional IP allowlist via DIAG_IP_ALLOWLIST env var (comma-separated IPs).
    Returns 404 if token is missing or incorrect, or if IP not in allowlist (not 401/403).
    """
    # Production kill-switch: require ENABLE_DIAG=1 in production
    if is_production and not is_truthy(os.getenv("ENABLE_DIAG", "0")):
        return jsonify({"ok": False}), 404
    
    # Optional IP allowlist check (executes before token validation)
    allowlist_str = (os.getenv("DIAG_IP_ALLOWLIST") or "").strip()
    if allowlist_str:
        # Get client IP using real_client_ip() helper
        client_ip = real_client_ip()
        
        # Check if client IP is in allowlist
        allowlist_ips = [ip.strip() for ip in allowlist_str.split(",") if ip.strip()]
        if client_ip not in allowlist_ips:
            return jsonify({"ok": False}), 404
    
    # Read expected token from environment
    expected_token = (os.getenv("DIAG_TOKEN") or "").strip()
    if not expected_token:
        return jsonify({"ok": False}), 404
    
    # Read provided token from header
    provided_token = (request.headers.get("X-DIAG-TOKEN") or "").strip()
    if not provided_token:
        return jsonify({"ok": False}), 404
    
    # Timing-safe comparison
    if not hmac.compare_digest(expected_token, provided_token):
        return jsonify({"ok": False}), 404
    
    # Authorized - return minimal response
    return jsonify({
        "ok": True,
        "time_utc": datetime.now(timezone.utc).isoformat().replace("+00:00", "Z"),
        "version": _app_version(),
    }), 200


