# --- Security Headers ---
@app.after_request
def add_security_headers(response):
    """
    Add security headers and CORS headers (if needed) to every response.
    This function runs after every request is processed.
    """
    # Add X-Request-ID header to response
    request_id = getattr(g, "request_id", None)
    if request_id:
        response.headers["X-Request-ID"] = request_id
    
    # Log request summary (one line per request)
    start_time = getattr(g, "request_start_time", None)
    duration_ms = "-"
    if start_time is not None:
        duration_ms = int((time.perf_counter() - start_time) * 1000)
    
    method = request.method
    path = request.path
    status = response.status_code
    user_id = session.get("user_id") if session else None
    remote_addr = request.headers.get("X-Forwarded-For") or request.remote_addr or "-"
    
    logger.info(
        "request_id=%s method=%s path=%s status=%s duration_ms=%s user_id=%s remote_addr=%s",
        request_id or "-",
        method,
        path,
        status,
        duration_ms,
        user_id if user_id else "-",
        remote_addr,
    )
    
    # CORS headers (only if flask_cors is not installed)
    if not cors_installed:
        # Read the incoming Origin header from the request
        incoming_origin = request.headers.get("Origin")
        
        # Check if the origin is in the whitelist
        allowed_origin = None
        if incoming_origin and incoming_origin in ALLOWED_ORIGINS:
            allowed_origin = incoming_origin
        
        # Only set CORS headers if the origin is whitelisted
        if allowed_origin is not None:
            response.headers["Access-Control-Allow-Origin"] = allowed_origin
            response.headers["Access-Control-Allow-Methods"] = "GET, POST, PUT, DELETE, OPTIONS"
            response.headers["Access-Control-Allow-Headers"] = "Content-Type, Authorization"
    
    # X-Content-Type-Options: Prevents browsers from MIME-sniffing (prevents XSS)
    response.headers.setdefault("X-Content-Type-Options", "nosniff")
    
    # X-Frame-Options: Prevents clickjacking by blocking iframe embedding
    response.headers.setdefault("X-Frame-Options", "DENY")
    
    # Referrer-Policy: Controls how much referrer information is sent
    # "strict-origin-when-cross-origin" sends full URL for same-origin, origin only for cross-origin
    response.headers.setdefault("Referrer-Policy", "strict-origin-when-cross-origin")
    
    # Permissions-Policy: Disables powerful browser features by default
    # This prevents websites from accessing geolocation, microphone, camera without explicit permission
    response.headers.setdefault(
        "Permissions-Policy",
        "geolocation=(), microphone=(), camera=(), payment=(), usb=()",
    )
    
    # Cache-Control & Pragma: Prevent caching of HTML responses only
    # This ensures that HTML responses aren't cached (prevents stale frontend updates)
    # API/JSON responses are left unchanged
    content_type = response.headers.get("Content-Type", "").lower()
    if content_type.startswith("text/html"):
        response.headers["Cache-Control"] = "no-store, no-cache, must-revalidate, max-age=0"
        response.headers["Pragma"] = "no-cache"
        response.headers["Expires"] = "0"

    # Content-Security-Policy: XSS baseline with nonce-based scripts
    nonce = getattr(g, "csp_nonce", None)
    csp_directives = [
        "default-src 'self'",
        "base-uri 'self'",
        "object-src 'none'",
        "frame-ancestors 'none'",
        "form-action 'self'",
        "img-src 'self' data:",
        "style-src 'self' 'unsafe-inline'",
    ]
    if nonce:
        csp_directives.append(f"script-src 'self' 'nonce-{nonce}'")
    else:
        # Fallback if nonce missing for some reason
        csp_directives.append("script-src 'self'")
    csp_directives.extend(
        [
            "connect-src 'self'",
            "upgrade-insecure-requests",
        ]
    )
    csp_value = "; ".join(csp_directives)
    csp_report_only = os.getenv("CSP_REPORT_ONLY") == "1"
    # Only set CSP headers if none are already present
    if "Content-Security-Policy" not in response.headers and "Content-Security-Policy-Report-Only" not in response.headers:
        csp_header_name = (
            "Content-Security-Policy-Report-Only" if csp_report_only else "Content-Security-Policy"
        )
        response.headers.setdefault(csp_header_name, csp_value)
    
    return response

# ============================================================================
# Error Handling
# ============================================================================
# Centralized error handlers to hide internal exceptions from users while
# logging them server-side. This prevents sensitive information (stack traces,
# SQL errors, file paths) from being exposed to clients.

# Configure logging for error handlers
logging.basicConfig(
    level=logging.ERROR,
    format='%(asctime)s - %(name)s - %(levelname)s - %(message)s'
)
error_logger = logging.getLogger(__name__)


@app.errorhandler(Exception)
def handle_unhandled_exception(error):
    """
    Global error handler for unhandled exceptions.
    Logs with request_id and traceback, then delegates to existing handlers.
    """
    request_id = getattr(g, "request_id", None)
    path = getattr(request, "path", "-")
    method = getattr(request, "method", "-")
    
    logger.exception(
        "event=unhandled_exception request_id=%s path=%s method=%s",
        request_id or "-",
        path,
        method,
    )
    
    # Let Flask's default error handling or specific error handlers take over
    # This ensures existing error handler behavior (status codes, responses) is preserved
    raise error


@app.errorhandler(500)
def handle_internal_server_error(error):
    """
    Handle HTTP 500 (Internal Server Error) exceptions.
    Logs full error details server-side, returns safe message to client.
    """
    request_id = getattr(g, "request_id", None)
    # Log the full error details including traceback on the server
    error_logger.error(
        f"Internal Server Error: {str(error)} request_id={request_id or '-'}",
        exc_info=True,
        extra={
            "path": request.path,
            "method": request.method,
            "remote_addr": request.remote_addr,
            "request_id": request_id,
        }
    )
    
    # Return appropriate response based on whether this is an API endpoint
    if request.path.startswith('/api/'):
        # API endpoints: return JSON error response
        return jsonify({
            "error": "server_error",
            "message": "An internal server error occurred. Please try again later."
        }), 500
    else:
        # Non-API routes: return simple HTML/string message
        return "Something went wrong. Please try again later.", 500


@app.errorhandler(404)
def handle_not_found(error):
    """
    Handle HTTP 404 (Not Found) errors.
    Logs the request details server-side, returns safe message to client.
    """
    # Log the 404 request for debugging (less critical than 500, so use warning level)
    error_logger.warning(
        f"404 Not Found: {request.method} {request.path}",
        extra={
            "path": request.path,
            "method": request.method,
            "remote_addr": request.remote_addr,
            "referrer": request.referrer
        }
    )
    
    # Return appropriate response based on whether this is an API endpoint
    if request.path.startswith('/api/'):
        # API endpoints: return JSON error response
        return jsonify({
            "error": "not_found",
            "message": "The requested resource was not found."
        }), 404
    else:
        # Non-API routes: return simple HTML/string message
        return "Page not found.", 404


@app.errorhandler(RateLimitExceeded)
def handle_ratelimit(e):
    """Handle rate limit exceeded errors - return 429 JSON response."""
    return jsonify({"ok": False}), 429


@app.errorhandler(CSRFError)
def handle_csrf_error(error):
    """
    Handle CSRF validation errors.
    Logs the error server-side, returns safe message to client.
    """
    # Log the CSRF error for debugging (without exposing details to client)
    error_logger.warning(
        f"CSRF validation failed: {request.method} {request.path}",
        extra={
            "path": request.path,
            "method": request.method,
            "remote_addr": request.remote_addr,
            "referrer": request.referrer
        }
    )
    
    # Return appropriate response based on whether this is an API endpoint
    if request.path.startswith('/api/'):
        # API endpoints: return JSON error response
        error_message = getattr(error, 'description', None) or str(error) or "CSRF validation failed"
        return jsonify({
            "ok": False,
            "error": "csrf_failed",
            "message": error_message
        }), 400
    else:
        # Non-API routes: flash friendly message and redirect
        flash('Session expired. Please reload the page and try again.', 'error')
        target = request.referrer or url_for('contact')
        return redirect(target)


