@limiter.limit("5 per hour", methods=["POST"])
def contact():
    """Contact form route"""
    if request.method == 'POST':
        # Honeypot spam protection
        company = request.form.get('company', '').strip()
        if company:
            return '', 204
        
        name = request.form.get('name', '').strip()
        email = request.form.get('email', '').strip()
