def remove_plural_ingredients():
    """Remove plural ingredient entries and keep only singular entries"""
    conn = sqlite3.connect(get_db_path())
    cursor = conn.cursor()
    
    # Comprehensive list of plural entries to remove
    plural_entries_to_remove = [
        'berries', 'acai berries', 'blueberries', 'strawberries', 'raspberries', 
        'blackberries', 'cranberries', 'cloudberries', 'elderberries', 
        'huckleberries', 'loganberries', 'boysenberries', 'mulberries', 'goji berries',
        'blue cheeses', 'chia seeds', 'flax seeds', 'flaxseeds', 'sesame seeds',
        'sunflower seeds', 'pumpkin seeds', 'hemp seeds', 'fennel seeds',
        'seeds', 'unsalted seeds', 'dried cranberries', 'fresh dates'
    ]
    
    removed_count = 0
    removed_ingredients = []
    removed_from = {'nutrition_facts': 0, 'ingredient_categories': 0, 'health_specific_serving_sizes': 0}
    
    for plural in plural_entries_to_remove:
        # Remove from nutrition_facts
        cursor.execute('DELETE FROM nutrition_facts WHERE ingredient = ?', (plural,))
        nf_count = cursor.rowcount
        if nf_count > 0:
            removed_from['nutrition_facts'] += nf_count
        
        # Remove from ingredient_categories
        cursor.execute('DELETE FROM ingredient_categories WHERE ingredient = ?', (plural,))
        ic_count = cursor.rowcount
        if ic_count > 0:
            removed_from['ingredient_categories'] += ic_count
        
        # Remove from health_specific_serving_sizes
        cursor.execute('DELETE FROM health_specific_serving_sizes WHERE ingredient = ?', (plural,))
        hss_count = cursor.rowcount
        if hss_count > 0:
            removed_from['health_specific_serving_sizes'] += hss_count
        
        if nf_count > 0 or ic_count > 0 or hss_count > 0:
            removed_count += 1
            removed_ingredients.append(plural)
            print(f"Removed plural: '{plural}'")
    
    conn.commit()
    conn.close()
    
    if removed_count > 0:
        print(f"✅ Removed {removed_count} plural ingredient entries")
        print(f"  - nutrition_facts: {removed_from['nutrition_facts']} entries")
        print(f"  - ingredient_categories: {removed_from['ingredient_categories']} entries")
        print(f"  - health_specific_serving_sizes: {removed_from['health_specific_serving_sizes']} entries")
        print(f"Removed ingredients: {', '.join(removed_ingredients[:20])}{'...' if len(removed_ingredients) > 20 else ''}")
    else:
        print("✅ No plural ingredient entries found to remove")
    return removed_count

