def is_blocked_ingredient_ingredient_tab(ingredient_name):
    """Check if an ingredient is blocked for Ingredient tab analysis (case-insensitive, trimmed)"""
    if not ingredient_name or not isinstance(ingredient_name, str):
        return False
    normalized = ingredient_name.lower().strip()
    return normalized in [name.lower().strip() for name in BLOCKED_INGREDIENT_NAMES_INGREDIENT_TAB]

