@csrf.exempt
@app.route("/api/me", methods=['PATCH'])
@login_required_single_session
def update_current_user():
    """Update the authenticated user's allowed fields (PoLP: users can only update their own data with allowlist)"""
    user_id = session.get("user_id")
    if not user_id:
        return jsonify({"error": "auth_required"}), 401
    
    try:
        data = request.get_json(force=True) or {}
        
        # Explicit allowlist of fields users can update themselves
        ALLOWED_UPDATE_FIELDS = ["email"]
        
        # Filter to only allowed fields
        updates = {}
        for field in ALLOWED_UPDATE_FIELDS:
            if field in data:
                updates[field] = data[field]
        
        if not updates:
            return jsonify({"error": "no_valid_fields"}), 400
        
        # Validate email format if email is being updated
        if "email" in updates:
            email = updates["email"]
            if not email or not isinstance(email, str) or "@" not in email:
                return jsonify({"error": "invalid_email"}), 400
            
            # Check if email is already taken by another user
            conn = get_conn()
            cursor = conn.cursor()
            query = db.prepare_query("SELECT id FROM users WHERE email = ? AND id != ?")
            cursor.execute(query, (email, user_id))
            existing = cursor.fetchone()
            if existing:
                conn.close()
                return jsonify({"error": "email_already_exists"}), 409
            
            # Update email
            update_query = db.prepare_query("UPDATE users SET email = ? WHERE id = ?")
            cursor.execute(update_query, (email, user_id))
            conn.commit()
            conn.close()
            
            return jsonify({"message": "user_updated", "updated_fields": ["email"]})
        
        return jsonify({"error": "no_updates"}), 400
        
    except Exception as e:
        return jsonify({"error": str(e)}), 500


@csrf.exempt
@app.route('/api/analyze', methods=['POST'])
def api_analyze():
    """Analyze ingredients endpoint - accepts both 'ingredients' array and 'query' string"""
    try:
        data = request.get_json(force=True) or {}
        
        # Handle both 'ingredients' array and 'query' string
        if 'ingredients' in data and isinstance(data['ingredients'], list):
            # Get original names if provided (for display purposes)
            original_names = data.get('originalNames', [])
            
            # Filter out blocked ingredients (defensive check - frontend should already filter)
            filtered_ingredients = []
            filtered_original_names = []
            blocked_ingredients = []
            for idx, ing in enumerate(data['ingredients']):
                if is_blocked_ingredient_ingredient_tab(ing):
                    blocked_ingredients.append(ing)
                    print(f"[ingredient-nutrition] Skipping blocked ingredient: \"{ing}\"")
                else:
                    filtered_ingredients.append(ing)
                    # Preserve corresponding original name if available
                    if idx < len(original_names):
                        filtered_original_names.append(original_names[idx])
                    else:
                        filtered_original_names.append(ing)  # Fallback to normalized name
            
            # If all ingredients are blocked, return appropriate response
            if len(filtered_ingredients) == 0 and len(blocked_ingredients) > 0:
                return jsonify({
                    "error": f"All ingredients are not supported in the analysis yet: {', '.join(blocked_ingredients)}",
                    "blocked_ingredients": blocked_ingredients,
                    "results": [],
                    "total_nutrition": {'calories': 0, 'protein': 0, 'carbs': 0, 'fat': 0, 'fiber': 0, 'sugar': 0, 'sodium': 0}
                }), 200  # Return 200 with error message, not 500
            
            # Use filtered ingredients
            data['query'] = ", ".join(filtered_ingredients)
            
            # Store original names for nlp_query to use
            data['_original_names'] = filtered_original_names
            
            # If some ingredients were blocked, add info to response
            if len(blocked_ingredients) > 0:
                # Store blocked list in request context for nlp_query to use
                data['_blocked_ingredients'] = blocked_ingredients
        elif 'query' not in data:
            return jsonify({"error": "No ingredients or query provided"}), 400
        
        # Call nlp_query - it will handle the data
        return nlp_query()
    except Exception as e:
        return jsonify({"error": str(e)}), 500


